import React, { useEffect, useState } from 'react';
import './App.css';
import { JulianDayCalculator } from './models/JulianDayCalculator';
import { LunarPhaseCalculator } from './models/LunarPhaseCalculator';
import { NRvector } from './models/NRvector';
import { NRmatrix } from './models/NRmatrix';
import { Doub, Int } from './models/Basic-types';

const App: React.FC = () => {

  const [year, setYear] = useState<number>(2024);
  const [month, setMonth] = useState<number>(1);
  const [day, setDay] = useState<number>(6);

  const [julianDay, setJulianDay] = useState<number | null>(null);
  const [dateInfo, setDateInfo] = useState<{ mm: number; id: number; iyyy: number } | null>(null);
  const [lunarPhase, setLunarPhase] = useState<{ jd: number; frac: number } | null>(null);

  const [vec, setVec] = useState<NRvector<Int>>(() => {
    const newVector = new NRvector<Int>(3);
    newVector.updateValues(Array.from({ length: 3 }, () => 0));
    return newVector;
  });


  const [matRows, setMatRows] = useState<number>(3);
  const [matCols, setMatCols] = useState<number>(3);
  const [matElements, setMatElements] = useState<number[][]>([
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9],
  ]);


  const [mat, setMat] = useState<NRmatrix<number>>(
    new NRmatrix<number>(matRows, matCols, 0)
  );

  const updateMatrix = () => {
    // Flatten the 2D array before passing it to NRmatrix constructor
    const flattenedElements: number[] = matElements.flat();
    setMat(new NRmatrix<number>(matRows, matCols, flattenedElements[0]));
  };

  const [vecLength, setVecLength] = useState<number>(3);
  const updateVector = () => {
    setVec((prevVec) => {
      // Create a copy of the previous vector
      const updatedVector = new NRvector<Int>(vecLength);

      // Update the values if lengths match
      if (prevVec.length === vecLength) {
        updatedVector.updateValues(prevVec.v);
      } else if (prevVec.length < vecLength) {
        // If the new length is greater, fill with zeros and update values
        const updatedValues = [...prevVec.v, ...Array.from({ length: vecLength - prevVec.length }, () => 0)];
        updatedVector.updateValues(updatedValues);
      }

      return updatedVector;
    });
  };




  useEffect(() => {
    // The effect will run whenever vec is updated
    console.log('Vector updated:', vec);
  }, [vec]);



  const updateYear = () => {
    setYear((prevYear) => prevYear + 1);
  };

  const renderVectorContent = () => {
    // Use map to wrap each element in a span and join them
    return vec.printToScreen().split(', ').map((item, index) => (
      <span key={index}>{item}</span>
    ));
  };

  const calculateValues = () => {
    const julianDayCalculator = new JulianDayCalculator();
    const lunarPhaseCalculator = new LunarPhaseCalculator();

    const jd = julianDayCalculator.julday(month, day, year);
    const di = julianDayCalculator.caldat(jd);
    const lp = lunarPhaseCalculator.flmoon(1, 0);

    setJulianDay(jd);
    setDateInfo(di);
    setLunarPhase(lp);
  };

  return (
    <div>
      <h3>Update Year:</h3>
      <label>
        Year:
        <input type="number" value={year} onChange={(e) => setYear(parseInt(e.target.value, 10))} />
      </label>
      <label>
        Month:
        <input type="number" value={month} onChange={(e) => setMonth(parseInt(e.target.value, 10))} />
      </label>
      <label>
        Day:
        <input type="number" value={day} onChange={(e) => setDay(parseInt(e.target.value, 10))} />
      </label>
      <button onClick={calculateValues}>Calculate</button>

      <div>
        <h3>Update Vector:</h3>
        <label>
          Vector Elements:
          <input type="number" value={vecLength} onChange={(e) => setVecLength(parseInt(e.target.value, 10))} />
        </label>
        <button onClick={updateVector}>Update Vector</button>
      </div>

      <div>
        <h3>Update Matrix:</h3>
        <label>
          Rows:
          <input
            type="number"
            value={matRows}
            onChange={(e) => setMatRows(parseInt(e.target.value, 10))}
          />
        </label>
        <label>
          Columns:
          <input
            type="number"
            value={matCols}
            onChange={(e) => setMatCols(parseInt(e.target.value, 10))}
          />
        </label>
        <button onClick={updateMatrix}>Update Matrix</button>
      </div>

      <div>
        <h2>Results:</h2>
        <p>Julian Day: {julianDay}</p>
        <p>Date Info: {dateInfo && `${dateInfo.mm}/${dateInfo.id}/${dateInfo.iyyy}`}</p>
        <p>Lunar Phase: {lunarPhase && `${lunarPhase.jd}, ${lunarPhase.frac}`}</p>

        {/* Display NRvector, NRmatrix, and NRMat3d values */}
        <div>
          <div>
            <h3>Example Usage:</h3>
            <p>NRvector&lt;Int&gt;: {vec.length} elements</p>
            <p>NRvector&lt;Int&gt;: {vec.printToScreen()}</p>
            {/* ... (other JSX code) */}
          </div>

          <p>NRmatrix&lt;number&gt;: {mat.rows} rows, {mat.cols} columns</p>
          <p>NRmatrix&lt;number&gt;: {<>{mat.printToScreen()}</>}</p>
          <div className="matrix-container">
            {mat.printToScreen().split('; ').map((row, rowIndex) => (
              <div key={rowIndex} className="matrix-row">
                {row.split(', ').map((element, colIndex) => (
                  <div key={colIndex} className="matrix-element">
                    {element}
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>


      </div>
    </div>
  );
};

export default App;
